changeSet.sql:

DO $$
DECLARE
    ts timestamp := NOW();
BEGIN
    INSERT INTO season (number, start_date) VALUES (5, ts);
    UPDATE season SET end_date = ts WHERE number = 4;
    UPDATE item SET
        attack = attack * 0.1,
        defense = defense * 0.1,
        health = health * 0.1,
        is_broken = TRUE;
    UPDATE personage SET money = money * 0.1;
    UPDATE pgroup SET money = money * 0.1;
    UPDATE personage SET energy=100, energy_recovery_notification_time = null WHERE energy != 100;
END $$;
/
